/*	Declare @CodSistema varchar(10) = 'Atacado'--*/Declare 	@Table varchar(max),	@Query nvarchar(max)	Select @Table = Case When @CodSistema = 'Atacado' Then 'IF_HIERARCHY_XREF_SAMS' Else 'IF_HIERARCHY_XREF' EndSet @Query = N'Select Distinct 	N4SAD_SECAOID		as Secao	, N4SAD_SECAO		as SecaoDesc	, N5SAD_LINHAID		as Linha	, N5SAD_LINHA		as LinhaDesc	, N6SAD_SUBLINHAID	as Sublinha	, N6SAD_SUBLINHA	as SublinhaDescFrom 	' + @Table + ''Execute sp_executesql @Query